mapper.select:
SELECT plan_num, -- 计划编号 area_city , -- 地市 grid_name, -- 网格名称 profession, -- 专业 backnet_type, -- 退网类型 neName, -- 网元名称 plan_backnet_time , -- 计划退网时间 logdate,-- 综合网管同步稽核时间 order_time,-- 资源系统状态变更时间 real_backnet_time, -- 实际退网时间 equip_status_new,-- 退网状态 ROUND(IFNULL(save_money,0) + IFNULL(power_dissipation,0) *24*1.1* IFNULL(electricity_price,5.5)*DATEDIFF(CONCAT(YEAR(plan_backnet_time),'-12-31'),plan_backnet_time)/1000/10000,2) plan_save_costs, -- 计划节省成本(因没有电费单价,待完善*electricity_price) ROUND(IFNULL(save_money,0) + IFNULL(power_dissipation,0) *24*1.1* IFNULL(electricity_price,5.5)*DATEDIFF(CONCAT(YEAR(real_backnet_time),'-12-31'),real_backnet_time)/1000/10000,2) real_save_costs, -- 实际节省成本 ROUND(IFNULL(power_dissipation,0) *24*1.1* IFNULL(electricity_price,5.5)*DATEDIFF(CONCAT(YEAR(real_backnet_time),'-12-31'),real_backnet_time)/1000/10000,2) real_save_electricity, -- 节省电费 save_money -- 节省维保费 FROM dwd_backnet_equip_analyse_test WHERE 1=1
and plan_num LIKE concat('%',#{planNum},'%')
and area_city=#{areaCity}
and grid_name LIKE concat('%',#{gridName},'%')
and profession=#{profession}
and equip_status_new=#{equipStatusNew}
and plan_time = concat(concat(#{planYear},'-'),#{planMonth})
and plan_time > concat(#{planYear},'-00') and plan_time < concat(#{planYear},'-13')
ORDER BY plan_backnet_time,area_city,neName DESC LIMIT ${(pageNo-1)*pageSize},${pageSize}

SELECT count(*) as total FROM dwd_backnet_equip_analyse_test WHERE 1=1
and plan_num LIKE concat('%',#{planNum},'%')
and area_city=#{areaCity}
and grid_name LIKE concat('%',#{gridName},'%')
and profession=#{profession}
and equip_status_new=#{equipStatusNew}
and plan_time = concat(concat(#{planYear},'-'),#{planMonth})
and plan_time > concat(#{planYear},'-00') and plan_time < concat(#{planYear},'-13')
